// Package hcloud provides a high-level wrapper around the official Hetzner Cloud client.
package hcloud

import (
	"context"
	"fmt"
	"log/slog"

	"github.com/cenkalti/backoff/v4"
	"github.com/hetznercloud/hcloud-go/v2/hcloud"
	"github.com/samber/lo"
	"github.com/samber/mo"
)

const (
	// LabelKey is the label key applied to all managed servers.
	LabelKey = "managed-by"

	// LabelValue is the label value for blackbsd-managed servers.
	LabelValue = "blackbsd-builder"

	// Label is the full label selector string.
	Label = LabelKey + "=" + LabelValue
)

// Client wraps the official hcloud.Client with domain-specific operations.
type Client struct {
	api *hcloud.Client
}

// NewClient creates a new Hetzner client with the given token.
func NewClient(token string) *Client {
	return &Client{
		api: hcloud.NewClient(hcloud.WithToken(token)),
	}
}

// NewClientWithOpts creates a new Hetzner client with custom options.
func NewClientWithOpts(opts ...hcloud.ClientOption) *Client {
	return &Client{
		api: hcloud.NewClient(opts...),
	}
}

// ListServers returns all servers matching the blackbsd label.
func (c *Client) ListServers(ctx context.Context) ([]*hcloud.Server, error) {
	var listOpts hcloud.ListOpts
	listOpts.LabelSelector = Label

	var serverListOpts hcloud.ServerListOpts
	serverListOpts.ListOpts = listOpts

	servers, err := c.api.Server.AllWithOpts(ctx, serverListOpts)
	if err != nil {
		return nil, fmt.Errorf("list servers: %w", err)
	}
	return servers, nil
}

// GetServer fetches a server by ID, returning None if not found.
func (c *Client) GetServer(ctx context.Context, id int64) mo.Option[*hcloud.Server] {
	server, _, err := c.api.Server.GetByID(ctx, id)
	if err != nil || server == nil {
		return mo.None[*hcloud.Server]()
	}
	return mo.Some(server)
}

// DeleteServer deletes a server. Returns true if deleted, false if not found.
func (c *Client) DeleteServer(
	ctx context.Context,
	server *hcloud.Server,
) (bool, error) {
	_, _, err := c.api.Server.DeleteWithResult(ctx, server)
	if err != nil {
		if hcloud.IsError(err, hcloud.ErrorCodeNotFound) {
			return false, nil
		}
		return false, fmt.Errorf("delete server %d: %w", server.ID, err)
	}

	slog.Info("server deleted", "id", server.ID, "name", server.Name)
	return true, nil
}

// CreateServer provisions a new build server with the blackbsd label.
func (c *Client) CreateServer(
	ctx context.Context,
	opts *CreateOpts,
) (*hcloud.Server, error) {
	var result hcloud.ServerCreateResult

	var serverType hcloud.ServerType
	serverType.Name = opts.ServerType

	var image hcloud.Image
	image.Name = opts.Image

	var location hcloud.Location
	location.Name = opts.Location

package hcloud_test

import (
	"context"
	"io"
	"net/http"
	"net/http/httptest"
	"strings"
	"testing"

	hcloudsdk "github.com/hetznercloud/hcloud-go/v2/hcloud"
	bsdhcloud "github.com/omarluq/hetzner-blackbsd/internal/hcloud"
	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

func writeJSON(t *testing.T, writer http.ResponseWriter, data string) {
	t.Helper()

	_, writeErr := writer.Write([]byte(data))
	require.NoError(t, writeErr)
}

func TestNewClient(t *testing.T) {
	t.Parallel()

	client := bsdhcloud.NewClient("test_token")

	assert.NotNil(t, client)
}

func TestListServers(t *testing.T) {
	t.Parallel()

	t.Run("returns servers with correct label", func(t *testing.T) {
		t.Parallel()

		serverJSON := `{
			"servers": [
				{"id": 1, "name": "server-1", "status": "running"},
				{"id": 2, "name": "server-2", "status": "running"}
			]
		}`

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.Header().Set("Content-Type", "application/json")
				writer.WriteHeader(http.StatusOK)
				writeJSON(t, writer, serverJSON)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		servers, err := client.ListServers(context.Background())

		require.NoError(t, err)
		assert.Len(t, servers, 2)
		assert.Equal(t, int64(1), servers[0].ID)
		assert.Equal(t, int64(2), servers[1].ID)
	})
}

func TestDeleteServer(t *testing.T) {
	t.Parallel()

	newTestServer := func(statusCode int, responseBody string) *httptest.Server {
		return httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.Header().Set("Content-Type", "application/json")
				writer.WriteHeader(statusCode)
				writeJSON(t, writer, responseBody)
			}))
	}

	var srv hcloudsdk.Server
	srv.ID = 42

	t.Run("deletes existing server and returns true", func(t *testing.T) {
		t.Parallel()

		testServer := newTestServer(http.StatusOK, `{}`)
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		deleted, err := client.DeleteServer(context.Background(), &srv)

		require.NoError(t, err)
		assert.True(t, deleted)
	})

	t.Run("returns false for 404", func(t *testing.T) {
		t.Parallel()

		testServer := newTestServer(http.StatusNotFound, `{"error": {"code": "not_found"}}`)
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		deleted, err := client.DeleteServer(context.Background(), &srv)

		require.NoError(t, err)
		assert.False(t, deleted)
	})
}

func TestGetServer(t *testing.T) {
	t.Parallel()

	t.Run("fetches server by ID", func(t *testing.T) {
		t.Parallel()

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, r *http.Request) {
				if strings.Contains(r.URL.String(), "/servers/42") {
					writer.Header().Set("Content-Type", "application/json")
					writer.WriteHeader(http.StatusOK)
					writeJSON(t, writer, `{
						"server": {"id": 42, "name": "test-server", "status": "running"}
					}`)
				}
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		result := client.GetServer(context.Background(), 42)

		assert.True(t, result.IsPresent())
		srv, _ := result.Get()
		assert.Equal(t, int64(42), srv.ID)
	})

	t.Run("returns None for 404", func(t *testing.T) {
		t.Parallel()

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.WriteHeader(http.StatusNotFound)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		result := client.GetServer(context.Background(), 42)

		assert.False(t, result.IsPresent())
	})
}

func TestServerStatus(t *testing.T) {
	t.Parallel()

	t.Run("returns status string for existing server", func(t *testing.T) {
		t.Parallel()

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.Header().Set("Content-Type", "application/json")
				writer.WriteHeader(http.StatusOK)
				writeJSON(t, writer, `{
					"server": {"id": 42, "status": "running"}
				}`)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		status := client.ServerStatus(context.Background(), 42)

		assert.Equal(t, "running", status)
	})

	t.Run("returns unknown for missing server", func(t *testing.T) {
		t.Parallel()

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.WriteHeader(http.StatusNotFound)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		status := client.ServerStatus(context.Background(), 42)

		assert.Equal(t, "unknown", status)
	})
}

func TestEnableRescue(t *testing.T) {
	t.Parallel()

	t.Run("enables rescue mode successfully", func(t *testing.T) {
		t.Parallel()

		body := `{"action": {"id": 1, "status": "running"}, "root_password": "test"}`

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.Header().Set("Content-Type", "application/json")
				writer.WriteHeader(http.StatusOK)
				writeJSON(t, writer, body)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		var srv hcloudsdk.Server
		srv.ID = 42

		result, err := client.EnableRescue(context.Background(), &srv, []int64{123})

		require.NoError(t, err)
		assert.True(t, result.IsOk())
		rescueResult, getErr := result.Get()
		require.NoError(t, getErr)
		assert.Equal(t, int64(1), rescueResult.Action.ID)
	})
}

func TestDisableRescue(t *testing.T) {
	t.Parallel()

	t.Run("disables rescue mode successfully", func(t *testing.T) {
		t.Parallel()

		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, _ *http.Request) {
				writer.Header().Set("Content-Type", "application/json")
				writer.WriteHeader(http.StatusOK)
				writeJSON(t, writer, `{"action": {"id": 1, "status": "running", "command": "disable_rescue"}}`)
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		var srv hcloudsdk.Server
		srv.ID = 42

		err := client.DisableRescue(context.Background(), &srv)
		require.NoError(t, err)
	})
}

func TestCreateServer(t *testing.T) {
	t.Parallel()

	t.Run("creates server with correct options", func(t *testing.T) {
		t.Parallel()

		var requestBodies []string
		testServer := httptest.NewServer(http.HandlerFunc(
			func(writer http.ResponseWriter, r *http.Request) {
				if r.Method == http.MethodPost && strings.Contains(r.URL.String(), "/servers") {
					body, readErr := io.ReadAll(r.Body)
					require.NoError(t, readErr)
					requestBodies = append(requestBodies, string(body))

					writer.Header().Set("Content-Type", "application/json")
					writer.WriteHeader(http.StatusCreated)
					writeJSON(t, writer, `{
						"server": {
							"id": 42,
							"name": "test-server",
							"status": "running"
						},
						"action": {"id": 1, "status": "running"}
					}`)
				}
			}))
		defer testServer.Close()

		client := bsdhcloud.NewClientWithOpts(hcloudsdk.WithEndpoint(testServer.URL))
		opts := &bsdhcloud.CreateOpts{
			Name:       "test-server",
			ServerType: "cpx31",
			Image:      "ubuntu-24.04",
			Location:   "fsn1",
			SSHKeyIDs:  []int64{123},
		}

		result, err := client.CreateServer(context.Background(), opts)

		require.NoError(t, err)
		assert.Equal(t, int64(42), result.ID)
		assert.Equal(t, "test-server", result.Name)

		require.Len(t, requestBodies, 1)
		assert.Contains(t, requestBodies[0], "test-server")
		assert.Contains(t, requestBodies[0], "cpx31")
		assert.Contains(t, requestBodies[0], "ubuntu-24.04")
		assert.Contains(t, requestBodies[0], "fsn1")
		assert.Contains(t, requestBodies[0], "managed-by")
		assert.Contains(t, requestBodies[0], "blackbsd-builder")
	})
}

	location.Name = opts.Location

	sshKeys := lo.Map(opts.SSHKeyIDs, func(id int64, _ int) *hcloud.SSHKey {
		var sshKey hcloud.SSHKey
		sshKey.ID = id
		return &sshKey
	})

	var createOpts hcloud.ServerCreateOpts
	createOpts.Name = opts.Name
	createOpts.ServerType = &serverType
	createOpts.Image = &image
	createOpts.Location = &location
	createOpts.SSHKeys = sshKeys
	createOpts.Labels = map[string]string{LabelKey: LabelValue}

	retryOperation := func() error {
		var err error
		result, _, err = c.api.Server.Create(ctx, createOpts)
		return err
	}

	if err := backoff.Retry(retryOperation, backoff.WithMaxRetries(backoff.NewExponentialBackOff(), 3)); err != nil {
		return nil, fmt.Errorf("create server %s: %w", opts.Name, err)
	}

	slog.Info("server created", "id", result.Server.ID, "name", result.Server.Name)
	return result.Server, nil
}

// EnableRescue enables rescue mode on the given server.
func (c *Client) EnableRescue(
	ctx context.Context,
	server *hcloud.Server,
	sshKeyIDs []int64,
) (mo.Result[hcloud.ServerEnableRescueResult], error) {
	sshKeys := lo.Map(sshKeyIDs, func(id int64, _ int) *hcloud.SSHKey {
		var sshKey hcloud.SSHKey
		sshKey.ID = id
		return &sshKey
	})

	var rescueOpts hcloud.ServerEnableRescueOpts
	rescueOpts.Type = hcloud.ServerRescueTypeLinux64
	rescueOpts.SSHKeys = sshKeys

	result, _, err := c.api.Server.EnableRescue(ctx, server, rescueOpts)
	if err != nil {
		return mo.Err[hcloud.ServerEnableRescueResult](err), err
	}

	slog.Info("rescue enabled", "server_id", server.ID, "action_id", result.Action.ID)
	return mo.Ok(result), nil
}

// DisableRescue disables rescue mode on the given server.
func (c *Client) DisableRescue(ctx context.Context, server *hcloud.Server) error {
	_, _, err := c.api.Server.DisableRescue(ctx, server)
	if err != nil {
		return fmt.Errorf("disable rescue for server %d: %w", server.ID, err)
	}
	return nil
}

// ServerStatus returns the status of a server by ID.
func (c *Client) ServerStatus(ctx context.Context, id int64) string {
	opt := c.GetServer(ctx, id)
	if server, ok := opt.Get(); ok {
		return string(server.Status)
	}
	return "unknown"
}

// CreateOpts defines options for creating a build server.
type CreateOpts struct {
	Name       string
	ServerType string
	Image      string
	Location   string
	SSHKeyIDs  []int64
}

