// runServerAction executes a server power action, logs success, and wraps errors.
func (c *Client) runServerAction(
	ctx context.Context,
	server *hcloud.Server,
	actionFn serverActionFunc,
	actionName string,
) error {
	_, _, err := actionFn(ctx, server)
	if err != nil {
		return fmt.Errorf("%s server %d: %w", actionName, server.ID, err)
	}

	slog.Info(actionName+" completed", "id", server.ID, "name", server.Name)
	return nil
}

// ResetServer performs a hardware reset on the given server.
func (c *Client) ResetServer(ctx context.Context, server *hcloud.Server) error {
	return c.runServerAction(ctx, server, c.api.Server.Reset, "reset")
}

// PowerOnServer powers on the given server.
func (c *Client) PowerOnServer(ctx context.Context, server *hcloud.Server) error {
	return c.runServerAction(ctx, server, c.api.Server.Poweron, "power on")
}

// PowerOffServer powers off the given server.
func (c *Client) PowerOffServer(ctx context.Context, server *hcloud.Server) error {
	return c.runServerAction(ctx, server, c.api.Server.Poweroff, "power off")
}

// WaitForAction waits until the given action completes.
func (c *Client) WaitForAction(ctx context.Context, action *hcloud.Action) error {
	if err := c.api.Action.WaitFor(ctx, action); err != nil {
		return fmt.Errorf("wait for action %d: %w", action.ID, err)
	}

	slog.Info("action completed", "id", action.ID)
	return nil
}

// WaitForServerStatus polls until the server reaches the target status.
func (c *Client) WaitForServerStatus(
	ctx context.Context,
	serverID int64,
	target hcloud.ServerStatus,
) error {
	expBackoff := backoff.NewExponentialBackOff()
	expBackoff.MaxElapsedTime = waitForStatusTimeout

	operation := func() error {
		server, _, getErr := c.api.Server.GetByID(ctx, serverID)
		if getErr != nil {
			return backoff.Permanent(fmt.Errorf("get server %d: %w", serverID, getErr))
		}

		if server == nil {
			return backoff.Permanent(fmt.Errorf("server %d: not found", serverID))
		}

		if server.Status != target {
			return fmt.Errorf(
				"server %d: status %s, want %s",
				serverID, server.Status, target,
			)
		}

		return nil
	}

	if err := backoff.Retry(operation, backoff.WithContext(expBackoff, ctx)); err != nil {
		return fmt.Errorf("wait for server %d status %s: %w", serverID, target, err)
	}

	slog.Info("server reached target status", "id", serverID, "status", target)
	return nil
}

// EnsureSSHKey finds an SSH key by name, creating it if it doesn't exist.
func (c *Client) EnsureSSHKey(
	ctx context.Context,
	name string,
	publicKey string,
) (*hcloud.SSHKey, error) {
	existing, _, err := c.api.SSHKey.GetByName(ctx, name)
	if err != nil {
		return nil, fmt.Errorf("get ssh key %q: %w", name, err)
	}

	if existing != nil {
		slog.Info("ssh key found", "name", name, "id", existing.ID)
		return existing, nil
	}

	var createOpts hcloud.SSHKeyCreateOpts
	createOpts.Name = name
	createOpts.PublicKey = publicKey

	created, _, err := c.api.SSHKey.Create(ctx, createOpts)
	if err != nil {
		return nil, fmt.Errorf("create ssh key %q: %w", name, err)
	}

	slog.Info("ssh key created", "name", name, "id", created.ID)
	return created, nil
}

// FindSSHKeyByFingerprint finds an SSH key by its fingerprint.
func (c *Client) FindSSHKeyByFingerprint(
	ctx context.Context,
	fingerprint string,
) mo.Option[*hcloud.SSHKey] {
	sshKey, _, err := c.api.SSHKey.GetByFingerprint(ctx, fingerprint)
	if err != nil || sshKey == nil {
		return mo.None[*hcloud.SSHKey]()
	}

	return mo.Some(sshKey)
}

